import './App.css';
import { io } from 'socket.io-client';
import { useState, useEffect } from 'react';
import { LiMensaje, UlMensajes } from './ui-components';

const socket = io('http://localhost:3000');

function App() {

  const [isConnected, setIsConnected] = useState(false);
  const [nuevoMensaje, setNuevoMensaje] = useState('');
  const [mensajes, setMensajes] = useState([]);
  const [usersOnline, setUsersOnline] = useState(0);

  useEffect(() => {

    socket.on('connect', () => setIsConnected(true));

    socket.on('chat_message', (data) => {
      setMensajes(mensajes => [...mensajes, data]);
    });

    socket.on('usersOnline', (data) => {
      setUsersOnline(data);
    });

    return () => {
      socket.off('connect');
      socket.off('chat_message');
      socket.off('usersOnline');
    }

  }, []);

  const enviarMensaje = () => {
    socket.emit('chat_message', {
      usuario: socket.id,
      mensaje: nuevoMensaje
    });
  }

  return (
    <div className="App">
      <h2>{isConnected ? 'CONECTADO' : 'NO CONECTADO'}</h2>
      <h2>Usuarios Online: {usersOnline}</h2>

      <ul class="list-group">
        {mensajes.map(mensaje => (
          <li class="list-group-item">{mensaje.usuario}: {mensaje.mensaje}</li>
        ))}

      </ul>

      <input
        type="text"
        onChange={e => setNuevoMensaje(e.target.value)}
      />
      <button className='btn btn-primary mx-2 my-2' onClick={enviarMensaje}>Enviar</button>
    </div>
  );
}

export default App;
